import React, { useState } from "react";

const TrainSearch = () => {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [trains, setTrains] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false); // New state to track search button click

  const handleSearch = () => {
    setSearchClicked(true); // Mark that search has been triggered
    // Example logic: Replace with your actual train fetching API call
    if (source && destination) {
      // Simulated train data
      const trainData = [
        { id: 1, name: "Express 101", time: "10:00 AM" },
        { id: 2, name: "Express 202", time: "2:00 PM" },
      ];
      // Simulate: no trains if source and destination are same
      if (source.toLowerCase() === destination.toLowerCase()) {
        setTrains([]);
      } else {
        setTrains(trainData);
      }
    } else {
      setTrains([]);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Train Search</h2>

      <div>
        <label>Source: </label>
        <input
          type="text"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
      </div>

      <div>
        <label>Destination: </label>
        <input
          type="text"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
      </div>

      <button onClick={handleSearch} style={{ marginTop: "10px" }}>
        Search Trains
      </button>

      {/* Show results only after clicking search */}
      {searchClicked && (
        <div style={{ marginTop: "20px" }}>
          {trains.length > 0 ? (
            <ul>
              {trains.map((train) => (
                <li key={train.id}>
                  {train.name} â€” {train.time}
                </li>
              ))}
            </ul>
          ) : (
            <p>No trains available</p>
          )}
        </div>
      )}
    </div>
  );
};

export default TrainSearch;
